INSERT INTO categories (line_id, title, description, created_at, updated_at) VALUES ('1', 'Premiums Cards & Envelopes', '<font color="#FFFF33">Premium Envelopes & Cards</font> have a smooth vellum finish, but are not translucent. <font color="#FFFFFF">Matching blank card stock</font> is available with a <font color="#FFFFFF">flat or embossed panel</font> on the face of the card. The envelopes are made with <font color="#FFFFFF">10% recycled content</font>. ', NOW(), NOW());
INSERT INTO categories (line_id, title, description, created_at, updated_at) VALUES ('1', 'Linen Cards & Envelopes', '<font color="#FFFF33">Linen Envelopes & Cards</font> are made of a paper with a linen texture. <font color="#FFFFFF">Matching blank card stock</font> is available with a <font color="#FFFFFF">flat or embossed panel</font> on the face of the card. ', NOW(), NOW());
INSERT INTO categories (line_id, title, description, created_at, updated_at) VALUES ('1', 'Mohawk Cards & Envelopes', '<font color="#FFFF33">Mohawk Cards & Envelopes</font> are <font color="#FFFFFF">Green Seal Certified</font> and <font color="#FFFFFF">100% post consumer waste (PCW) recycled</font> content. They also use <font color="#FFFFFF">wind power to generate 100% of the electricity used in this paper production!</font> ', NOW(), NOW());
INSERT INTO categories (line_id, title, description, created_at, updated_at) VALUES ('1', 'Deckled Cards & Envelopes', '<font color="#FFFF33">Deckled Edge Envelopes & Card Stock</font> have deckled edges (gracefully torn). The blank cards are made of <font color="#FFFFFF">ultra felt paper</font> and the envelopes are <font color="#FFFFFF">deckled along the edge</font> of their flaps. Many of the envelopes are <font color="#FFFFFF">made of 100% recycled content</font>. ', NOW(), NOW());
INSERT INTO categories (line_id, title, description, created_at, updated_at) VALUES ('1', 'Basis Envelopes', '<font color="#FFFF33">Basis Envelopes & Cards</font> are paper envelopes that come in <font color="#FFFFFF">many colors and sizes</font>. These envelopes are from the same maker of the <a href="/?cat|ASTR" class="FeatureLink">Astrobrights</a>, but <font color="#FFFFFF">Basis Envelopes are thicker</font>. ', NOW(), NOW());
INSERT INTO categories (line_id, title, description, created_at, updated_at) VALUES ('1', 'Brown Bag Envelopes', '<font color="#FFFF33">Brown Bag Envelopes</font> have the look and feel of the traditional large brown bags used in grocery stores. <font color="#FFFFFF">Made from 100% recycled material</font>, these envelopes give a natural earthy look to your cards and invitations. ', NOW(), NOW());
INSERT INTO categories (line_id, title, description, created_at, updated_at) VALUES ('1', 'Astrobrights Envelopes', '<font color="#FFFF33">Astrobrights Envelopes & Card Stock</font> are paper envelopes that come in <font color="#FFFFFF">many colors and sizes</font>. If you\'re looking for envelopes that are a little thicker try the <a href="/?cat|BASI" class="FeatureLink">Basis Cards and Envelopes</a>. ', NOW(), NOW());
INSERT INTO categories (line_id, title, description, created_at, updated_at) VALUES ('1', 'Foil-lined Envelopes', '<font color="#FFFF33">Foil-lined Envelopes</font> are white paper envelopes with a vellum finish and the inside has a <font color="#FFFFFF">silver or gold foil lining</font>. These envelopes are <font color="#FFFFFF">great for any special occasion</font>. ', NOW(), NOW());
INSERT INTO categories (line_id, title, description, created_at, updated_at) VALUES ('1', 'Leather Metallic Envelopes', '<font color="#FFFF33">Eastern Metallic Cards & Envelopes with a Leather Finish</font> are stunning to look at as they have a nice sheen on them. The paper\'s surface <font color="#FFFFFF">resembles a fine leather texture</font>. The cards are white on the inside. ', NOW(), NOW());
INSERT INTO categories (line_id, title, description, created_at, updated_at) VALUES ('1', 'Brushed Metallic Envelopes', '<font color="#FFFF33">Eastern Metallic Cards & Envelopes with a Brushed Finish</font> are stunning to look at as they have a nice sheen on them. The paper\'s surface <font color="#FFFFFF">resembles a brushed metal texture</font>. The cards are white on the inside. ', NOW(), NOW());
INSERT INTO categories (line_id, title, description, created_at, updated_at) VALUES ('1', 'Stardream Envelopes', '<font color="#FFFF33">Stardream Envelopes</font> have a unique <font color="#FFFFFF">metallic and smooth iridescent finish</font>. Resembles metallic-speck paint often used on new car finishes. ', NOW(), NOW());
INSERT INTO categories (line_id, title, description, created_at, updated_at) VALUES ('1', 'Notables Envelopes', '<font color="#FFFF33">Notables Envelopes</font> for note cards in nice earth tone colors. <font color="#FFFFFF">Made out of 100% recycled content.</font> ', NOW(), NOW());
INSERT INTO categories (line_id, title, description, created_at, updated_at) VALUES ('1', 'Vellum Envelopes', '<font color="#FFFF33">Translucent Vellum Envelopes</font> are frosted envelopes that come in <font color="#FFFFFF">clear and pastel colors</font>. ', NOW(), NOW());
INSERT INTO categories (line_id, title, description, created_at, updated_at) VALUES ('2', 'Bags: Adhesive Seal', '<font color="#FFFF33">Crystal Clear Plastic Bags</font> are known for having the <font color="#FFFFFF">highest clarity</font> and for their <font color="#FFFFFF">superior protection from finger prints</font> and the rigors of the retail environment. These bags have <font color="#FFFFFF">closing flaps</font> and a <font color="#FFFFFF">resealable adhesive strip</font>. ', NOW(), NOW());
INSERT INTO categories (line_id, title, description, created_at, updated_at) VALUES ('2', 'Bags: Open-ended', '<font color="#FFFF33">Crystal Clear No Flap Bags</font> are known for having the <font color="#FFFFFF">highest clarity</font> and for their <font color="#FFFFFF">superior protection from finger prints</font> and the rigors of the retail environment. These bags have a <font color="#FFFFFF">"no flap" top opening</font> allowing for <font color="#FFFFFF">cards to be easily removed</font>. ', NOW(), NOW());
INSERT INTO categories (line_id, title, description, created_at, updated_at) VALUES ('2', 'Bags: Hanging', '<font color="#FFFF33">Crystal Clear Hanging Bags</font> have a <font color="#FFFFFF">hang strip</font>, allowing <font color="#FFFFFF">greeting cards and stationery to hang on pegs</font>, and a flap and <font color="#FFFFFF">resealable adhesive strip on the bottom of the bag</font>. ', NOW(), NOW());
INSERT INTO categories (line_id, title, description, created_at, updated_at) VALUES ('2', 'Bio-degradable Bags', '<font color="#FFFF33">Clear Biodegradable Bags</font> are a high clarity and <font color="#FFFFFF">Earth-friendly</font> packaging solution that are <font color="#FFFFFF">made from plants!</font> Lactic Acid (not acid-free) from corn is used to create the material Polyactide (PLA). <font color="#FFFFFF">Certified biodegradable and compostable</font>. ', NOW(), NOW());
INSERT INTO categories (line_id, title, description, created_at, updated_at) VALUES ('2', 'Card Jackets', '<font color="#FFFF33">Greeting Card Jackets</font> work like a dust jacket for individual cards. The jacket allows you to <font color="#FFFFFF">display the inside of your card while protecting them from handling</font>. The raw material includes 10% recycled poly. ', NOW(), NOW());
INSERT INTO categories (line_id, title, description, created_at, updated_at) VALUES ('2', 'Soft-folding Boxes', '<font color="#FFFF33">Crystal Clear Plastic Boxes</font> are an ideal choice for <font color="#FFFFFF">packaging sets of greeting cards</font>. Store them flat and pop them open when you are ready. <font color="#FFFFFF">Boxes come with <a href="/?cat|STIC" class="FeatureLink">Clear Round Stickers</a></font> for sealing them shut. Don\'t forget to <font color="#FFFFFF">decorate with <a href="/?cat|LOOP" class="FeatureLink">Elastic Stretch Loops</a>!</font> ', NOW(), NOW());
INSERT INTO categories (line_id, title, description, created_at, updated_at) VALUES ('2', 'Hanging Boxes', '<font color="#FFFF33">Hanging Stationery Boxes</font> have a reinforced <font color="#FFFFFF">hang strip on the top for easy retail display</font>. They are made from high quality high density PVC and are easily folded. Store flat and pop them up when you are ready to use them. ', NOW(), NOW());
INSERT INTO categories (line_id, title, description, created_at, updated_at) VALUES ('2', 'Vinyl Folders', '<font color="#FFFF33">Vinyl Card Holders</font> work like wallets to <font color="#FFFFFF">protect sets of greeting cards</font>. Other Vinyl Holders include <font color="#FFFFFF">checkbook covers</font>, <font color="#FFFFFF">business card holders</font>, <font color="#FFFFFF">hanging bookmark sleeves</font>, and <font color="#FFFFFF">hanging magnet sleeves</font>. ', NOW(), NOW());
INSERT INTO categories (line_id, title, description, created_at, updated_at) VALUES ('3', 'Organza Ribbon', '<font color="#FFFF33">Organza Ribbon</font> comes in a wide variety of colors that will <font color="#FFFFFF">add a touch of class</font> to your greeting cards and invitations. Use it to <font color="#FFFFFF">bundle your cards</font> or as part of your <font color="#FFFFFF">artistic card creations</font>. ', NOW(), NOW());
INSERT INTO categories (line_id, title, description, created_at, updated_at) VALUES ('3', 'Hang Tabs', '<font color="#FFFF33">Stick-on Hang Tabs</font> are used to <font color="#FFFFFF">hang products that don\'t have hangers</font> and are small and light enough to be hung up. Hang Tabs are <font color="#FFFFFF">clear</font> and have a <font color="#FFFFFF">moderate-strength adhesive</font> on the lower half of them. ', NOW(), NOW());
INSERT INTO categories (line_id, title, description, created_at, updated_at) VALUES ('3', 'Stretch Loops', '<font color="#FFFF33">Elastic Stretch Loops</font> are elastic bands that are used to wrap and <font color="#FFFFFF">decorate <a href="/?cat|PBOX" class="FeatureLink">Plastic Boxes</a></font> or <font color="#FFFFFF">bundle envelopes and cards</font>. There are 2 varieties of stretch loops, which include a <font color="#FFFFFF">round elastic</font> type in a variety of colors and a <font color="#FFFFFF">semi-clear flat stretchy vinyl</font> type. ', NOW(), NOW());
INSERT INTO categories (line_id, title, description, created_at, updated_at) VALUES ('3', 'Stickers', '<font color="#FFFF33">Clear Round Stickers</font> are <font color="#FFFFFF">already included when you buy <a href="/?cat|PBOX" class="FeatureLink">Plastic Boxes</a></font>, which are used to seal them. However, you may also purchase these stickers <font color="#FFFFFF">in sheets of 20</font> as they are popular for other artistic purposes. ', NOW(), NOW());
INSERT INTO categories (line_id, title, description, created_at, updated_at) VALUES ('4', 'Shipping Boxes for Cards', '', NOW(), NOW());
INSERT INTO categories (line_id, title, description, created_at, updated_at) VALUES ('4', 'Grab Bags', '<font color="#FFFF33">Plastic Grab Bags</font> (aka Merchandising Bags) are the <font color="#FFFFFF">most economical solution</font> for a point-of-sale retail carry bag. They are especially <font color="#FFFFFF">great for light-weight purchases such as stationery and greeting cards</font> as they are about the same thickness as a standard plastic grocery produce bag. ', NOW(), NOW());
INSERT INTO categories (line_id, title, description, created_at, updated_at) VALUES ('4', 'Plastic Gift Bags', '<font color="#FFFF33">Translucent Plastic Gift Bags</font> are a great way to package wedding gifts and birthday presents. Greeting cards like to be paired up with gifts... <font color="#FFFFFF">Try selling gift bags along side your greeting cards!</font> ', NOW(), NOW());
